// Samples from Weibull distribution with
// a=5432 and b=3.21
data = [
3303.
3172.
2473.
5602.
3109.
4415.
6471.
5952.
3945.
3534.
]
parmhat = distfun_wblfitmm(data)
a=parmhat(1);
b=parmhat(2);
// Compare the (mean,variance) of the
// distribution against the data :
// must be equal.
[M,V]=distfun_wblstat(a,b)
