m=1000;
x1=distfun_unifrnd(0,1,m,1);
x2=distfun_unifrnd(0,1,m,1);
x3=distfun_unifrnd(0,1,m,1);
y1=2*x1.*x2+x3;
y2=-3*x1+x2.^2-2*x3;
y3=sin(x1)-3*x2+3*x3;
x=[x1,x2,x3];
y=[y1,y2,y3];
xlabels=["X1","X2","X3"];
ylabels=["Y1","Y2","Y3"];
scf();
plotmatrix(x,y,"xlabels",xlabels,"ylabels",ylabels);


