refsection.programlisting:
x=distfun_chi2rnd(3,1000,1);
scf();
histo(x);
xtitle("Chi-Square random numbers","X","Frequency")

// Set the number of classes
scf();
histo(x,10);

// Set the edges
scf();
X=distfun_unifrnd(0,1,1000,1);
edges = 0:0.2:1.;
histo(X,edges);

// See without scaling
scf();
histo(x,[],%f);
// See with scaling
scf();
histo(x,[],%t);

// See various colors and styles
scf();
histo(x,[],[],1);
scf();
histo(x,[],[],2);
scf();
histo(x,[],[],3);
